import React from 'react';
import MainScreen from "../../components/MainScreen";
import { Link } from 'react-router-dom';
import { Button, Card, Badge } from 'react-bootstrap';
import notes, { } from "../../data/notes";

const MyNotes = () => {
  const deleteHandler = (id) => {
