  }
  return (
    <MainScreen title="Welcome Back Archana">
      <Link to="createnote">
        <Button tyle={{ marginLeft: 10, marginBottom: 6 }} size="lg">
          Create new note
        </Button>
        </Link>
        {
          notes.map(note => (
            <Card style={{ margin: 10 }}>
              <Card.Header style={{ display: 'flex' }}>
                <span
                  style={{
                    color: "black",
                    textDecoration: "none",
                    flex: 1,
                    cursor: "pointer",
                    alignSelf: "center",
                    fontSize: 18,
                  }}
                >{note.title}</span>

                <div>
                  <Button href={`/note/${note._id}`}>Edit</Button>
                  <Button variant="danger" className="mx-2" onClick={() => deleteHandler(note)}>Delete</Button>
                </div>
              </Card.Header>
              <Card.Body>
                <h4>
                  <Badge variant='success'>
                    Category - {note.category}
                  </Badge>
                </h4>
                <blockquote className="blockquote mb-0">
                  <p>
                  {note.content}
                  </p>
                  <footer className="blockquote-footer">
                   Created on - date
                  </footer>
                </blockquote>
              </Card.Body>

            </Card>

          ))
        }

      




    </MainScreen>
  )
}

export default MyNotes;
